    //Create a compound file
    string FILENAME = "MyFileName.cfs";
    CompoundFile ncf = new CompoundFile();

    CFStorage l1 = ncf.RootStorage.AddStorage("Storage Level 1");

    l1.AddStream("l1ns1");
    l1.AddStream("l1ns2");
    l1.AddStream("l1ns3");
    CFStorage l2 = l1.AddStorage("Storage Level 2");
    l2.AddStream("l2ns1");
    l2.AddStream("l2ns2");

    ncf.Save(FILENAME);
    ncf.Close();

