    const String FILENAME = "CompoundFile.cfs";
    CompoundFile cf = new CompoundFile(FILENAME);

    CFStorage st = cf.RootStorage.GetStorage("MyStorage");
    cf.Close();

    try
    {
        byte[] temp = st.GetStream("MyStream").GetData();
        
